def get_place_coordinates(place_name):
    """
    Get coordinates for a given place name.
    
    Args:
        place_name (str): Name of the place
        
    Returns:
        list: [latitude, longitude] or None if place not found
    """
    return PLACE_COORDINATES.get(place_name)

def get_all_place_names():
    """
    Get list of all place names that have coordinates.
    
    Returns:
        list: List of place names
    """
    return list(PLACE_COORDINATES.keys())
